ion&#34;&gt;&lt;span class=&#34;keyword&#34;&gt;auto&lt;/span&gt; &lt;span class=&#34;title&#34;&gt;prework&lt;/span&gt;&lt;span class=&#34;params&#34;&gt;(&lt;span class=&#34;type&#34;&gt;int&lt;/span&gt; n)&lt;/span&gt; -&amp;gt; &lt;span class=&#34;type&#34;&gt;void&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;function&#34;&gt;&lt;/span&gt;&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    fact[&lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;] = finv[&lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;] = &lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;keyword&#34;&gt;for&lt;/span&gt;(&lt;span class=&#34;type&#34;&gt;int&lt;/span&gt; i = &lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;; i &amp;lt;= n; i += &lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;) fact[i] = fact[i&lt;span class=&#34;number&#34;&gt;-1&lt;/span&gt;] * i % p;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;keyword&#34;&gt;for&lt;/span&gt;(&lt;span class=&#34;type&#34;&gt;int&lt;/span&gt; i = &lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;; i &amp;lt;= n; i += &lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;) finv[i] = &lt;span class=&#34;built_in&#34;&gt;ksm&lt;/span&gt;(fact[i], p&lt;span class=&#34;number&#34;&gt;-2&lt;/span&gt;);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;function&#34;&gt;&lt;span class=&#34;keyword&#34;&gt;auto&lt;/span&gt; &lt;span class=&#34;title&#34;&gt;C&lt;/span&gt;&lt;span class=&#34;params&#34;&gt;(&lt;span class=&#34;type&#34;&gt;int&lt;/span&gt; n, &lt;span class=&#34;type&#34;&gt;int&lt;/span&gt; m)&lt;/span&gt; -&amp;gt; &lt;span class=&#34;type&#34;&gt;long&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;function&#34;&gt;&