&gt;  &lt;/mtext&gt;&lt;mi&gt;a&lt;/mi&gt;&lt;mi&gt;n&lt;/mi&gt;&lt;mi&gt;d&lt;/mi&gt;&lt;mtext&gt;  &lt;/mtext&gt;&lt;mi&gt;a&lt;/mi&gt;&lt;mo&gt;&amp;lt;&lt;/mo&gt;&lt;mi&gt;c&lt;/mi&gt;&lt;/mrow&gt;&lt;/mstyle&gt;&lt;/mtd&gt;&lt;/mtr&gt;&lt;/mtable&gt;&lt;/mrow&gt;&lt;/mrow&gt;&lt;/mstyle&gt;&lt;/mtd&gt;&lt;/mtr&gt;&lt;/mtable&gt;&lt;annotation encoding=&#34;application/x-tex&#34;&gt;\begin{aligned}

f(a,b,c,n) = 
    \begin{cases}

    \frac{n \, (n + 1)}{2}
    *
    \left\lfloor \frac{a}{c} \right\rfloor
    +
    (n + 1)
    *
    \left\lfloor \frac{b}{c} \right\rfloor
    +
    f(a \% c,\, b \% c,\, c,\, n)
    &amp;amp; , 
    a \ge c \;or\; b \ge c

    \\

    nm
    -
    f(c,\, c-b-1,\, a,\, m - 1)

    &amp;amp; ,
    a &amp;lt; c \;and\; a &amp;lt; c
    \end{cases}

\end{aligned}
