h(a,b,c,n) = 
    \begin{cases}

    \frac{n (n + 1) ( n * 2 +1) \lfloor a / c \rfloor}{6}
    +
    \frac{n (n + 1) \lfloor b / c \rfloor}{2}
    +
    h(a \% c,\, b \% c,\, c, n)

    &amp;amp; , 
    a \ge c \;or\; b \ge c

    \\

    \frac{1}{2}
    n m (n + 1)
    -
    \frac{1}{2}
        f(c,\, c - b - 1,\, a, m - 1)
    -
    \frac{1}{2}
        g(c,\, c - b - 1,\, a, m - 1) 

    &amp;amp; ,
    a &amp;lt; c \;and\; a &amp;lt; c
    \end{cases}

\end{aligned}
