g(a,b,c,n)

&amp;amp; =
\sum _{i = 0} ^{n} 
    \left\lfloor
        \frac{ai + b}{c}
    \right\rfloor
    ^{2}
\\

&amp;amp; =
2 \sum _{i = 0} ^{n}
    \sum _{j = 0} ^{ \lfloor \frac{ai + b}{c} \rfloor} j
-
\sum _{i = 0} ^{n}
    \left\lfloor \frac{ai + b}{c} \right\rfloor
\\

&amp;amp; =
2 \sum _{i = 0} ^{n}
    \sum _{j = 1} ^{m} 
        j \left[ \; 
            j &amp;lt; \left\lfloor \frac{ai + b}{c} \right\rfloor
        \; \right]
-
f(a, b, c, n)
\\

&amp;amp; =
2 \sum _{i = 0} ^{n}
    \sum _{j = 1} ^{m} 
        j \left[ \; 
            \frac{jc + c - b - 1}{a} &amp;lt; i
        \; \right]
-
f(a, b, c, n)
\\

&amp;amp; =
2 \sum _{i = 0} ^{n}
    \sum _{j = 0} ^{m-1} 
        (j+1) \left[ \; 
            \frac{jc + c - b - 1}{a} &amp;lt; i
        \; \right]
-
f(a, b, c, n)
\\

&amp;amp; =
2 \sum _{j = 0} ^{m-1} 
    \sum _{i = 0} ^{n}
        (j+1) \left[ \; 
            \frac{jc + c - b - 1}{a} &amp;lt; i
        \; \right]
-
f(a, b, c, n)
\\

&amp;amp; =
2 \sum _{j = 0} ^{m-1} 
    (j+1) \left( 
        n 
        -
        \left\lfloor \frac{jc + c - b - 1}{a} \right\rfloor
    \right)
-
f(a, b, c, n)
\\

&amp;amp; =
2 n \sum _{j = 1} ^{m} j
-
2 \sum _{j = 0} ^{m-1} 
    (j+1) \left\lfloor \frac{jc + c - b - 1}{a} \right\rfloor

-
f(a, b, c, n)
\\

&amp;amp; =
n m (m + 1)
-
2 h(c,\, c - b - 1,\, a,\, m - 1)
-
2 f(a,\, b,\, c,\, m-1)
-
f(a, b, c, n)

\end{aligned}
