o&gt;&lt;/mrow&gt;&lt;/mstyle&gt;&lt;/mtd&gt;&lt;/mtr&gt;&lt;/mtable&gt;&lt;annotation encoding=&#34;application/x-tex&#34;&gt;\begin{aligned}
h(n) &amp;amp; = h(n-1) + \sum _{i=2}^{n}g(i)*h(n-i)
\\
h(n) &amp;amp; = h(n-1) + \sum _{i=2}^{n}h(i-2)*h(n-i)
\\
h(n) &amp;amp; = h(n-1) + \sum _{i=0}^{n-2}h(i)*h(n-2-i)
\end{aligned}
