o stretchy=&#34;false&#34;&gt;[&lt;/mo&gt;&lt;mi&gt;d&lt;/mi&gt;&lt;mo&gt;≤&lt;/mo&gt;&lt;mi&gt;a&lt;/mi&gt;&lt;mo stretchy=&#34;false&#34;&gt;]&lt;/mo&gt;&lt;/mrow&gt;&lt;/mstyle&gt;&lt;/mtd&gt;&lt;/mtr&gt;&lt;/mtable&gt;&lt;annotation encoding=&#34;application/x-tex&#34;&gt;\begin{aligned}

&amp;amp;\sum_{i = 1}^{n}
    \sum_{j = 1}^{m}
        \operatorname{lcm}(i,j)
            [\gcd(i, j) \le a]
\\

= 
&amp;amp;\sum_{i = 1}^{n}
    \sum_{j = 1}^{m}
        \frac{i \cdot j}{\gcd(i, j)}
            [\gcd(i, j) \le a] 
\\

=
&amp;amp;\sum_{d = 1}^{A}
    \sum_{i = 1}^{n}
        \sum_{j = 1}^{m}
            \frac{i \cdot j}{d}
                [\gcd(i, j) = d] [d \le a]
\\

= 
&amp;amp;\sum_{d = 1}^{A}
    \sum_{i = 1}^{\lfloor\frac{n}{d}\rfloor}
        \sum_{j = 1}^{\lfloor\frac{m}{d}\rfloor}
            ijd [\gcd(i, j) = 1] [d \le a]
\\

=
&amp;amp;\sum_{d = 1}^{A}
    \sum_{i = 1}^{\lfloor\frac{n}{d}\rfloor}
        \sum_{j = 1}^{\lfloor\frac{m}{d}\rfloor}
            ijd 
                \sum_{k | \gcd(i, j)} \mu(k) 
                    [d \le a]
\\

=
&amp;amp;\sum_{d = 1}^{A} d
    \sum_{k = 1}^{\lfloor\frac{A}{d}\rfloor}
        k^2 \mu(k)
            \sum_{i = 1}^{\lfloor\frac{n}{dk}\rfloor} i
            \sum_{j = 1}^{\lfloor\frac{m}{dk}\rfloor} j
                [d \le a]
\\

=
&amp;amp;\sum_{d = 1}^{A} d
    \sum_{k = 1}^{\lfloor\frac{A}{d}\rfloor}
        k^2 \mu(k) 
        \operatorname{S}(\lfloor\frac{n}{dk}\rfloor)
        \operatorname{S}(\lfloor\frac{m}{dk}\rfloor)
            [d \le a]
\\

=
&amp;amp;\sum_{T = 1}^{A}
    \operatorname{S}(\lfloor\frac{n}{T}\rfloor)
    \operatorname{S}(\lfloor\frac{m}{T}\rfloor)
        \sum_{d | T} \frac{T^2}{d}\mu(\frac{T}{d})
            [d \le a]

\end{aligned}
